void test_max_pool2(int *correct_cases, int *total_cases)
{
    *correct_cases += max_pool2_sequential();
    *total_cases += 1;
    *correct_cases += max_pool2_random_1();
    *total_cases += 1;
    *correct_cases += max_pool2_random_2();
    *total_cases += 1;
    *correct_cases += max_pool2_random_3();
    *total_cases += 1;
}