void test_conv2(int *correct_cases, int *total_cases)
{
    *correct_cases += conv2_zeros();
    *total_cases += 1;

    *correct_cases += conv2_sequential();
    *total_cases += 1;

    *correct_cases += conv2_random_1();
    *total_cases += 1;

    *correct_cases += conv2_random_2();
    *total_cases += 1;

    *correct_cases += conv2_random_3();
    *total_cases += 1;
}
